function updateClock(){var now=moment(),second=6*now.seconds(),minute=6*now.minutes()+second/60,hour=now.hours()%12/12*360+90+minute/12;$("#hour").css("transform","rotate("+hour+"deg)"),$("#minute").css("transform","rotate("+minute+"deg)"),$("#second").css("transform","rotate("+second+"deg)")}function timedUpdate(){updateClock(),setTimeout(timedUpdate,1e3)}timedUpdate();